import React from "react";
import axios from 'axios';
import { Button } from "@mui/material";
import "./Book.css";
import {Link} from 'react-router-dom';
import { useNavigate } from "react-router-dom";
import { connect } from "react-redux";
import {setcartdata} from '../../Actions';
import { handleBreakpoints } from "@mui/system";

const Book = (props,{cart_data,setcartdata}) =>{
    const history = useNavigate();
    const{ _id,name,image,description,qty,unitprice} = props.book;
    const deleteHandler = async() =>{
        await axios.delete(`http://localhost:5000/books/${_id}`)
        .then(res=>res.data)
        .then(()=>history("/"))
        .then(()=>history("/books"));
    }

    const Handler = async(id,name,description,qty,unitprice,image) =>{
        await setcartdata(id,name,description,qty,unitprice,image)
    }

    return(
        <div className="card" key={_id}>
            <img src={image} alt={image}/>
            <article>BY {description}</article>
            <h3>{name}</h3>
            <h2>Rs .{unitprice}</h2>
            <Button LinkComponent={Link} to={`/books/${_id}`} sx={{mt:'auto'}}>Update</Button>
            <Button onClick={deleteHandler} sx={{mt:'auto'}}>Delete</Button>
            <Button  sx={{mt:'auto'}} onClick={()=>Handler(_id,name,description,qty,unitprice,image)}>Add Cart</Button>
        </div>
    )
}

const mapSatateToProps = state =>({
    cart_data:state.cartreducer
})
export default connect(mapSatateToProps,{setcartdata})(Book);
