const initialState = {
    name:null
};

export default function cartreducer(state=initialState,action){
    const {type,payload} = action;
    switch(type){
        case "ADDCART":
            return{...state,name:payload}
        case "REMOVEITEM":
            return{...state,name:null}
        default:
            return state    

    }
}
